//Java Program to Swap Two Numbers

package assignment;

public class Que_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, temp=0;
		a=4;
		b=9;
		System.out.println(a+" & "+b);
		//swapping
		temp=a;
		a=b;
		b=temp;
		System.out.println(a+" & "+b);
	}

}
